import './App.css';
import Navbar from './componenets/Navbar';
import TextForm from './componenets/TextForm';
import React, {useState} from 'react';
import Alert from './componenets/Alert';
import About from './componenets/About';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";


function App() {

  const [mode, setMode] = useState("light");
  const [alert, setAlert] = useState(null);

  const showAlert = (message, type)=> {
      setAlert({
        msg: message,
        type: type
      })
      setTimeout(()=> {
        setAlert(null);
      },1500);
  }

  const toggleMode = ()=> {
    if(mode === 'light')
    {
      setMode("dark")
      document.body.style.backgroundColor = '#042743';
      showAlert("Dark mod ehas been enabled","success");
      // document.title = 'TextUtils - Dark Mode';
    }
    else{
      setMode('light');
      document.body.style.backgroundColor = 'white';
      showAlert("Light mod ehas been enabled","success");
      // document.title = 'TextUtils - Light Mode';
    }
  }
  return (
    <>
     <Router>
     <Navbar title="TextUtils" mode={mode} toggleMode={toggleMode}/>
     <Alert alert={alert}/>
     <div className="container my-3">
       <Routes>
        <Route exact path="/about" element={<About mode={mode}/>}/> 

        <Route exact path="/" element={<TextForm showAlert={showAlert} heading="Try TextUtils - word Counter, Character Counter, 
        Remove extra spaces" mode={mode}/>}/>


      </Routes>
     </div>
     </Router>
    </>
  );
}

export default App;
